void	typedef_parser::prt_line () // Print current line being parsed.
{
      char* ls = token.start;
      char* le = token.end;
      while (*ls != '\n' && *ls != 0) ls--;
      if (*ls == 0) // Already printed this one?
		{
         return;
		}
      *ls = 0;
      ls++;
      while (*le != '\n') le++;
      *le = 0;
		if (*ls != 26) // EOF?
		{
			printf  (            "%6d  %s\n", linenumb, ls);
			fprintf (outputdesc, "%6d  %s\n", linenumb, ls);
		}
		else
		{
	      printf  (            "\n");
		   fprintf (outputdesc, "\n");
		}
      *le = '\n';
}

